package allprograms;

public class Foreachloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 // Array declaration
        int arr[] = { 1, 2, 3, 4, 5 };
        
        // Using for-each loop to 
        // print each element
        for (int e : arr) {
            System.out.print(e  + " ");
        }
    }

}


			  
		   
		      
		       
